import { Card, Col, Container, Row } from "react-bootstrap";

export default function ThreeColumns() {
    const deals = [
        {
            img: "https://veggiesabroad.com/wp-content/uploads/2024/01/halifax-harbour-with-colorful-sky.jpg",
            city: "Halifax",
            info: "2h 40m, direct",
            dates: "Mon 27/10 → Thu 30/10",
            price: "from C$ 175",
        },
        {
            img: "https://images.unsplash.com/photo-1507525428034-b723cf961d3e?auto=format&fit=crop&w=800&q=80",
            city: "Fort Lauderdale",
            info: "3h 20m, direct",
            dates: "Thu 23/10 → Thu 30/10",
            price: "from C$ 188",
        },
        {
            img: "https://images.unsplash.com/photo-1505761671935-60b3a7427bad?auto=format&fit=crop&w=800&q=80",
            city: "Orlando",
            info: "3h 10m, direct",
            dates: "Sun 26/10 → Fri 31/10",
            price: "from C$ 196",
        },
        {
            img: "https://content.r9cdn.net/rimg/dimg/de/1d/0c45439e-city-2279-1708cd59b68.jpg?crop=true&width=1020&height=498",
            city: "Washington, D.C.",
            info: "1h 33m, direct",
            dates: "Tue 21/10 → Fri 24/10",
            price: "from C$ 257",
        },
    ];

    return (
        <Container id="deals" className="py-5">
            <h2 className="fw-bold mb-4">Travel deals under CA$ 300</h2>
            <Row>
                {deals.map((deal, i) => (
                    <Col key={i} lg={3} md={6} sm={12} className="mb-4">
                        <Card className="deal-card shadow-sm border-0">
                            <Card.Img variant="top" src={deal.img} className="deal-img" />
                            <Card.Body>
                                <Card.Title className="fw-bold">{deal.city}</Card.Title>
                                <Card.Text className="text-muted mb-1">{deal.info}</Card.Text>
                                <Card.Text className="text-muted mb-2">{deal.dates}</Card.Text>
                                <Card.Text className="fw-bold text-dark">{deal.price}</Card.Text>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
        </Container>
    );
}
